import{transformTime,calcTime}from"./sleep-cycle.min.js";const btn=document.querySelector("button");btn.addEventListener("click",()=>{const starOrEndSleep=document.querySelector("#start-sleep").value,qtd=document.querySelector("#qt-cicle").value;let calcTimes=calcTime(qtd);atualizarDados(transformTime(qtd),starOrEndSleep,calcTimes)});const atualizarDados=(timeSleep,starOrEndSleep,toSleepOrWakeUp)=>{printResult(1,timeSleep),printResult(2,starOrEndSleep),printResult(3,toSleepOrWakeUp)},printResult=(index,time)=>{const resultado=document.querySelector("#resultado .resume__item:nth-child("+index+")>h2");resultado.innerText=time},Radio=document.querySelectorAll('[type="radio"]');Radio.forEach(element=>{element.addEventListener("click",event=>{const textInput=document.querySelector(".texto>span"),textResult=document.querySelector("#resultado .resume__item:nth-child(2)>div>span"),textToSleepOrWakeUp=document.querySelector("#resultado .resume__item:nth-child(3)>div>span");"start"==event.target.value?(textInput.innerHTML=textResult.innerHTML=" dormir:",textToSleepOrWakeUp.innerHTML="acordar"):(textInput.innerHTML=textResult.innerHTML=" acordar:",textToSleepOrWakeUp.innerHTML="dormir")})});const Range=document.getElementById("qt-cicle");Range.addEventListener("change",event=>{document.getElementById("respostaRange").innerHTML=event.target.value});
